select retweet_count,textbody, screen_name, category, sub_category  from user u join newtweet t on u.screen_name = t.posting_user where t.month_posted = 1 AND t.year_posted = 2016 order by t.retweet_count desc limit 5;
SELECT COUNT( distinct state) as `state_num`, group_concat(distinct state) as `states`, hashtagname FROM tagged t inner join newtweet n inner join user u on u.screen_name = n.posting_user on t.tid = n.tid  where year_posted = 2016 and state != 'na' GROUP BY t.hashtagname ORDER BY `state_num` DESC LIMIT 5;
select distinct screen_name, state from user u join tagged t join newtweet n on t.tid = n.tid on n.posting_user = u.screen_name where t.hashtagname in ('HappyNewYear','NewYear','NewYears','NewYearsDay') order by u.numFollowers desc limit 12;
select screen_name, sub_category, numFollowers from user where sub_category = 'GOP' order by numFollowers desc limit 5;
select screen_name, sub_category, numFollowers from user where sub_category = 'Democrat' order by numFollowers desc limit 5;
select distinct hashtagname, state from user u inner join newtweet n on u.screen_name = n.posting_user inner join tagged t on t.tid = n.tid where state in('ohio','alaska','alabama') and month_posted = 1 and year_posted = 2016 order by hashtagname;
select textbody, hashtagname, screen_name, sub_category from newtweet n inner join tagged t on t.tid= n.tid inner join user u on u.screen_name = n.posting_user  where (sub_category = 'GOP' or sub_category = 'Democrat') and state = 'ohio' and month_posted = 1 and year_posted = 2016 and hashtagname = 'ohio' limit 5;
select screen_name, state, group_concat(url) from user u join newtweet n on u.screen_name = n.posting_user join urlused r on r.tid= n.tid where u.sub_category = 'GOP' and month_posted = 1 and year_posted = 2016 group by screen_name;
SELECT m.screen_name , state, group_concat(distinct posting_user) FROM mentioned m inner join newtweet n inner join user u on u.screen_name = n.posting_user on m.tid = n.tid  where sub_category = 'GOP' and year_posted = 2016 and month_posted in (1) GROUP BY m.screen_name ORDER BY COUNT(m.screen_name) DESC LIMIT 5;
SELECT hashtagname, COUNT(hashtagname) AS `num_uses` FROM tagged t inner join newtweet n inner join user u on u.screen_name = n.posting_user on t.tid = n.tid where sub_category = 'GOP' and year_posted = 2016 and month_posted in (1,2,3) GROUP BY hashtagname ORDER BY `num_uses` DESC LIMIT 5;
